const occupancy = msg.payload.occupancy;
const countdown = 10;

flow.set("PIR_SUBFLOW", {
    "occupancy": occupancy,
    "countdown": countdown,
    "lastOutput" : null
});

// node.warn(`Message[${JSON.stringify(msg)}]`);
const LIGHT_ON = true;
const LIGHT_OFF = false;

function outputOne(msg) {
    node.warn(`Output 1[${JSON.stringify(msg)}]`)
    return [msg, null];
}
function outputTwo(msg) {
    node.warn(`Output 2[${JSON.stringify(msg)}]`)
    return [null, msg];
}
const occupancy = flow.get("PIR_SUBFLOW").occupancy;
const countDownValue = flow.get("PIR_SUBFLOW").countdown;
let lastOutput = flow.get("PIR_SUBFLOW").lastOutput;

const isCountingOn = msg.payload;
const payloadInfoMsg = isCountingOn ? `Incoming payload[TRUE] light on` : `Incoming payload[FALSE] light off`;
node.warn(payloadInfoMsg);

 // Timer has stopped but occupancy still true
if (!isCountingOn && occupancy) {
    node.warn(`Timer has stopped but occupancy still ${occupancy}`);
    // topic === 'control' restarts Timer
    msg.topic = 'control';
    msg.payload = countDownValue;
    return outputTwo(msg);
}

let currentOutput = isCountingOn ? LIGHT_ON : LIGHT_OFF;
const logMsg = `Last output[${lastOutput}] current[${currentOutput}]`;
if (currentOutput !== lastOutput) {
    node.warn(logMsg + " send");
    flow.set("PIR_SUBFLOW", {
        "occupancy": occupancy,
        "countdown": countDownValue,
        "lastOutput": currentOutput
    });
    return outputOne(msg);
}
node.warn(logMsg + " don't send");
return [null,null];

node.warn(msg);

const dict = new Map();
dict.set("IdleState", { on: true, ps: 3 });
dict.set("WorkState", { on: false });
dict.set("OvertimeState", { on: true, ps: 9 });
dict.set("AlertState", { on: true, ps: 5 });
dict.set("RestState", { on: true, ps: 4 });
dict.set("Unknown", { on: false });

const state = msg.payload;
if (dict.has(state)) {
  node.warn(`Return[${JSON.stringify(dict.get(state))}]`)
  msg.payload = dict.get(state);
} else {
    dict.error("Unknown state.", msg);
    node.warn(`Return[${JSON.stringify(dict.get('Unknown'))}]`)
    msg.payload = dict.get("Unknown");
}

return msg;

